import * as React from 'react';
import Card from '@mui/material/Card';
import { CardHeader } from '@mui/material';

import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import { addToCart } from '../../store/cart';
import { connect } from 'react-redux';
import './products.css'


function Products(props) {
    return (
        <>
            <div className="cart">
                <section >
                    <div>
                        {
                            props.products.map((product) => (

                                product.category === props.categories.activeCategory ?
                                    <Card style={{ backgroundColor: '' }}>
                                        <CardHeader

                                            title={product.name}
                                            subheader={product.category}
                                        />

                                        <img src={product.image} style={{ width: '200px', height: '200px' }}  ></img>
                                        <CardHeader
                                            title={'Price  ' + product.price + '$'}
                                            subheader={'In Stock  ' + product.inStock + '  Pcs'}
                                        />
                                        <Button
                                            data-testid='button'
                                            variant="contained"
                                            color="primary"
                                            style={{ width: 100 + '%' }}
                                            onClick={() => {
                                                props.addToCart(product);
                                            }}
                                        >
                                            ADD TO CART
                                        </Button>

                                    </Card>
                                    :
                                    null
                            ))
                        }
                    </div>

                </section>
            </div>
        </>
    );
}


const mapStateToProps = (state) => {
    return { products: state.products, categories: state.categories, cart: state.cart };
};

const mapDispatchToProps = { addToCart };


export default connect(mapStateToProps, mapDispatchToProps)(Products);
